import React, { Fragment } from "react";

// import components

const AddFilesBtn = ({ showTranscript, hideAddFileBtn }) => {
  return (
    <Fragment>
      {hideAddFileBtn && (
        <button onClick={showTranscript}>
          Add files <span>Supported formats: mp3, flac, wav</span>
        </button>
      )}
    </Fragment>
  );
};

export default AddFilesBtn;
