import React, { Fragment, useState } from "react";

// import components
import AddFilesBtn from "./AddFilesBtn";
import AddFiles from "./AddFiles";

const TranscriptContainer = () => {
  const [hideAddFilesBtn, setHideFilesBtn] = useState(false);

  const showAddFiles = () => {
    setHideFilesBtn(!hideAddFilesBtn);
  };

  return (
    <Fragment>
      <AddFilesBtn setHideFilesBtn={setHideFilesBtn} />
      <AddFiles hideAddFilesBtn={hideAddFilesBtn} />
    </Fragment>
  );
};

export default TranscriptContainer;
